import { Grid, CircularProgress } from '@material-ui/core';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './styles';
import {fetchUsers} from '../../actions/users';
import {useDispatch} from 'react-redux';
import { Card, CardActions, CardContent, CardMedia,  } from '@material-ui/core/';
import { Container, Grow } from '@material-ui/core';



const Users = () => {
    const dispatch = useDispatch();
   const users = useSelector((state) => state.users);
  const classes = useStyles();
  const [searchString,setSearch]= useState("");
  const [filter,setFilter]=useState([]);
  const history = useHistory();
  const User = JSON.parse(localStorage.getItem('profile'));

  useEffect(() => {
    
    dispatch(fetchUsers());
    
  },[ dispatch]);

  const search = async e =>  {
    e.preventDefault();

      if(users){
        
        users.map(function(user){
          if(searchString==user.name && searchString!==User.result.name){
        if(filter.length)
        if(filter.indexOf(user)==-1) 
        if(filter.friends.indexOf(user.name)==-1)
        {
       
          filter.push(user);
        history.push("/users")
      
        }
             setFilter(filter)
          }
        })
      }
     
  };

  const sendReqquestTo = (e,userID) => {
    // dispatch update message  action from controller
    e.preventDefault();
    console.log(userID) 
  }
  
  
  return (
  
   
    <Grid>
      <Grid >
        <div >
  
    <form autoComplete="off" noValidate align="center">
    <Grid item xs={6} m={7}></Grid>
    <Button variant="outlined" margin="none" color="primary"  onClick={search}>Search users </Button>
    <Grid item xs={6} m={7}></Grid>
    <TextField variant="outlined" name="search"  margin="normal" value ={searchString} onChange={e => setSearch(e.target.value)} />

</form>

</div> 
</Grid>
     
  <div align="center"> 
     <Grid className={classes.container} container alignItems="stretch" spacing={3}>
        
           {
           filter.map((user) => (
            <div align="center"> 
<Grid key={user._id} item xs={6} sm={6} md={8} >
      <Card className={classes.card}>
       <CardMedia className={classes.media} />
        <Typography className={classes.title} gutterBottom variant="h5" component="h2"></Typography>
        <CardContent>
         <Typography variant="body2" color="textSecondary" component="p"></Typography>
       </CardContent>
       <CardActions className={classes.cardActions}>
         <Button size="medium" color="primary" onClick={ (e) => sendReqquestTo(e,user._id) }>  add friend {user.name}</Button>
        </CardActions> 
      </Card>
 </Grid>
 </div>                                  )                  
                                         )                            
                                            }


                                          
         </Grid>                          
      
       </div>
           
    
         </Grid>

        

  );
};

export default Users;
