import React, { useState, useEffect } from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { AppBar, Typography, Toolbar, Avatar, Button } from '@material-ui/core';
import useStyles from './styles';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';



const Navbar = () => {

  const classes = useStyles();
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();

  
  const logout = () => {
    dispatch({ type: 'LOGOUT' });

    history.push('/auth');

    setUser(null);
  };


  useEffect(() => {
    const token = user?.token;

    if (token) {
      const decodedToken = decode(token);

      if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    }

    setUser(JSON.parse(localStorage.getItem('profile')));
  }, [location]);
 

  return (
    <AppBar className={classes.appBar} position="static" color="inherit">
      <div className={classes.brandContainer}>
        <Typography className={classes.heading} variant="h2" align="center">SociApp</Typography>
      
      </div>
      <Toolbar className={classes.toolbar}>
          
          <div className={classes.profile}>
            <Typography className={classes.userName} variant="h6"></Typography>
            <Button variant="contained" className={classes.login} color="primary" >Login</Button>
          </div>
       
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
