import { Grid, CircularProgress } from '@material-ui/core';
import { useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './styles';
import {fetchUsers} from '../../actions/users';
import {useDispatch} from 'react-redux';
import { Card, CardActions, CardContent, CardMedia,  } from '@material-ui/core/';
import { Container, Grow } from '@material-ui/core';



const Users = () => {
    const dispatch = useDispatch();
   const users = useSelector((state) => state.users);
  const classes = useStyles();
  const [searchString,setSearch]= useState("");

  useEffect(() => {
    
    dispatch(fetchUsers());
    
  },[ dispatch]);

  const search = async e =>  {
    e.preventDefault();
   console.log(searchString)
 
      
      console.log(users)
    
      
  };
  if(users){
    users.map(function(user){
      if(searchString==user.name){
   
        return(

            <Card>{user.name}</Card>
        )
      }
    })
  }
  
  return (
  
    <Grid container justify="space-between" alignItems="center" spacing={3}>
    <div>
      <Grid>
        <Grid item xs={6} sm={7}></Grid>
    <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} >
    <Button variant="contained" color="primary" size="small"   onClick={search}>Search users </Button>
<TextField margin ="none"name="search " variant="outlined" label="search users ..."  value ={searchString} onChange={e => setSearch(e.target.value)} />

</form>

       </Grid>

      
         <Grow in>
    <Container>
      <Grid container justify="space-between" alignItems="stretch" spacing={1}>
        <Grid item xs={6} ml={10}></Grid>

           {users.map((user) => (
    
      <Card className={classes.card}>
       <CardMedia className={classes.media} />
        <Typography className={classes.title} gutterBottom variant="h5" component="h2"></Typography>
        <CardContent>
         <Typography variant="body2" color="textSecondary" component="p"></Typography>
       </CardContent>
       <CardActions className={classes.cardActions}>
         <Button size="small" color="primary" > {user.name}</Button>
        </CardActions> 
      </Card>
                                       )                  
                                         )                            
                                            }
       </Grid>
       </Container>
            </Grow>         
    
  </div>
   
</Grid>

  );
};

export default Users;
