import { Grid, CircularProgress } from '@material-ui/core';
import { useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './styles';
import {fetchUsers} from '../../actions/users';
import {useDispatch} from 'react-redux';

const Users = () => {
    const dispatch = useDispatch();
   const users = useSelector((state) => state.users);
  const classes = useStyles();
  const user= JSON.parse(localStorage.getItem('profile'))
  const [searchString,setSearch]=useState("");

  const search =  e =>  {
    e.preventDefault();
   console.log(searchString)
      dispatch(fetchUsers());
      
  };

  return (
   
    <Paper>
    <Grid item xs={12} sm={7}>
<form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} >
<TextField name="search " variant="outlined" label="search users ..." fullWidth value ={searchString} onChange={e => setSearch(e.target.value)} />
<Button variant="contained" color="primary" size="lg"  fullWidth onClick={search}>Search users </Button>
</form>
</Grid>
</Paper>

      
       
     
          
  
   
 
    
  );
};

export default Users;
