import { Grid, CircularProgress } from '@material-ui/core';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './styles';
import {fetchUsers} from '../../actions/users';
import {useDispatch} from 'react-redux';
import { Card, CardActions, CardContent, CardMedia,  } from '@material-ui/core/';
import { Container, Grow } from '@material-ui/core';



const Users = () => {
    const dispatch = useDispatch();
   const users = useSelector((state) => state.users);
  const classes = useStyles();
  const [searchString,setSearch]= useState("");
  const [filter,setFilter]=useState([]);
  const history = useHistory();


  useEffect(() => {
    
    dispatch(fetchUsers());
    
  },[ dispatch]);

  const search = async e =>  {
    e.preventDefault();
  console.log(searchString)
      if(users){
    
        users.map(function(user){
          if(searchString==user.name){
            console.log(user.name)
             
              filter.push(user)
             setFilter(filter)
             history.push('/users')
          }
        })
      }
     
  };

  
  
  return (
  
   
    <Grid>
      <Grid>
        
    <form autoComplete="off" noValidate  >
    <Grid item xs={6} m={7}></Grid>
    <Button variant="contained" color="primary"  onClick={search}>Search users </Button>
    <Grid item xs={6} m={7}></Grid>
<TextField name="search " label="search users ..."  value ={searchString} onChange={e => setSearch(e.target.value)} />

</form>

       </Grid>

      
         <Grow in>
    <Container>
      <Grid container justify="space-between" alignItems="center" spacing={1}>
        <Grid item xs={6} ml={10}></Grid>
        <Grid container justify="space-between" alignItems="stretch" spacing={20}>
           {filter.map((user) => (
     <Grid container justify="space-between" alignItems="center" spacing={1}>
      <Card className={classes.card}>
       <CardMedia className={classes.media} />
        <Typography className={classes.title} gutterBottom variant="h5" component="h2"></Typography>
        <CardContent>
         <Typography variant="body2" color="textSecondary" component="p"></Typography>
       </CardContent>
       <CardActions className={classes.cardActions}>
         <Button size="small" color="primary" > {user.name}</Button>
        </CardActions> 
      </Card>
      </Grid>
                                       )                  
                                         )                            
                                            }
                                            </Grid>
       </Grid>
       </Container>
            </Grow>         
    
            </Grid>
   


  );
};

export default Users;
