export default (users = [], action) => {
    switch (action.type) {
      case 'FETCH_ALL_USERS':
        return action.payload;
      case 'F_Req':
        return users.map((user)=>user._id==action.payload._id?action.payload:user)
      case 'F_Acc':
        
         const updated= users.map((user)=>(user._id==action.payload.updatedUser._id ?action.payload.updatedUser:user ))
         users=updated.map((user)=>(user._id==action.payload.updatedSender._id ?action.payload.updatedSender:user ))
         const token=JSON.parse(localStorage.getItem('profile')).token;
        
         const obj={result:action.payload.updatedSender,token}
         localStorage.setItem('profile', JSON.stringify(obj));
         return users
      case 'F_Ref':
         const tok=JSON.parse(localStorage.getItem('profile')).token;
         const ob={result:action.payload,tok}
         localStorage.setItem('profile', JSON.stringify(ob));
         return users.map((user)=>user._id==action.payload._id?action.payload:user)
      default:
        return users;
    }
  };
