import { Grid, CircularProgress } from '@material-ui/core';
import { useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './styles';
import {fetchUsers} from '../../actions/users';
import {useDispatch} from 'react-redux';
import { Card, CardActions, CardContent, CardMedia,  } from '@material-ui/core/';
import User from './user';


const Users = () => {
    const dispatch = useDispatch();
   const users = useSelector((state) => state.users);
  const classes = useStyles();
  const [searchString,setSearch]= useState("");

  useEffect(() => {
    
    dispatch(fetchUsers());
    
  },[ dispatch]);

  const search = async e =>  {
    e.preventDefault();
   console.log(searchString)
 
      
      console.log(users)
    
      
  };
  if(users){
    users.map(function(user){
      if(searchString==user.name){
   
        return(

            <Card>{user.name}</Card>
        )
      }
    })
  }
  
  return (
  
     <container >
    <div>
    <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} >
<TextField name="search " variant="outlined" label="search users ..." fullWidth value ={searchString} onChange={e => setSearch(e.target.value)} />
<Button variant="contained" color="primary" size="small" fullWidth  onClick={search}>Search users </Button>
</form>
</div>
<div >
       <Grid container direction="row" justify="center" alignItems="center"
>
    
         <div align="center" class="wrapper"> 
       
           {users.map((user) => (
     

         <Button variant="contained"  color="secondary" >{user.name}</Button>
          
       
      
      ))}
      </div>
      
    
    </Grid>
    </div>
</container>

  );
};

export default Users;
