export default (users = [], action) => {
    switch (action.type) {
      case 'FETCH_ALL_USERS':
        return action.payload;
      case 'F_Req':
        return users.map((user)=>user._id==action.payload._id?action.payload:user)
      case 'F_Acc':
           
          return users.map((user)=>user._id==user._id)
      
      default:
        return users;
    }
  };
