import * as api from '../api/index.js';

export const fetchUsers = () => async (dispatch) => {
    try {
      const { data } = await api.fetchUsers();
  
      dispatch({ type: 'FETCH_ALL_USERS', payload: data });
    } catch (error) {
      console.log(error);
    }
  };
